(function() {
  $(function() {
    var updateAddCustomerLink;
    $('.typeahead').typeahead('close');
    $("#event_time_start").on('change', function() {
      var endTime, startTime;
      startTime = moment(this.value, 'HH:mma');
      startTime.add(2, 'hours');
      endTime = startTime.format('h:mma');
      $("#event_time_end").val(endTime).material_select();
      return updateAddCustomerLink();
    });
    $("#event_date").on('change', function() {
      return updateAddCustomerLink();
    });
    $("#event_time_end").on('change', function() {
      return updateAddCustomerLink();
    });
    updateAddCustomerLink = function() {
      var addCustomerLink, eventDate, eventTimeEnd, eventTimeStart;
      eventDate = $("#event_date").val();
      eventDate = moment(eventDate, 'D MMMM, YYYY');
      eventDate = eventDate.format('YYYY-MM-DD');
      eventTimeStart = $("#event_time_start").val();
      eventTimeStart = moment(eventTimeStart, 'HH:mma');
      eventTimeStart = eventTimeStart.format('HH:mm');
      eventTimeEnd = $("#event_time_end").val();
      eventTimeEnd = moment(eventTimeEnd, 'HH:mma');
      eventTimeEnd = eventTimeEnd.format('HH:mm');
      addCustomerLink = '/customers/new?start_time=' + eventDate + 'T' + encodeURIComponent(eventTimeStart) + '&end_time=' + encodeURIComponent(eventTimeEnd);
      return $("#add-customer-link").attr('href', addCustomerLink);
    };
    return $("#button").click(function(event) {
      event.preventDefault();
      return updateAddCustomerLink();
    });
  });

}).call(this);
(function() {
  $(function() {
    var formatTel, numToTel;
    numToTel = function(phones) {
      var i, len, phone, results;
      results = [];
      for (i = 0, len = phones.length; i < len; i++) {
        phone = phones[i];
        results.push("<p><a href='tel:" + phone[0] + "'>" + (formatTel(phone[0])) + "</a> " + phone[1] + "</p>");
      }
      return results;
    };
    formatTel = function(number) {
      return number.replace(/(\d\d\d)(\d\d\d)(\d\d\d\d)/, '($1) $2-$3');
    };
    return $('#calendar').fullCalendar({
      header: {
        left: 'prev,next today',
        center: 'title',
        right: 'month,agendaWeek,agendaDay'
      },
      defaultDate: gon.date,
      editable: false,
      slotEventOverlap: false,
      eventLimit: false,
      allDaySlot: false,
      height: "auto",
      minTime: "06:00:00",
      maxTime: "21:00:00",
      timeFormat: ' ',
      defaultView: "agendaWeek",
      eventBorderColor: "#313131",
      events: gon.events,
      eventRender: function(event, element) {
        element.click(function() {
          var $invoiceBtn;
          $('#modal-customer-name').text(event.title);
          $('#modal-edit-btn').attr('href', event.edit_url);
          $('#modal-date').text(event.date);
          $('#modal-time').text(event.time);
          $('#modal-phones').html(numToTel(event.phones));
          $('#modal-address').text(event.address);
          $('address').each(function() {
            var link;
            link = '<a href=\'http://maps.google.com/maps?q=' + encodeURIComponent($(this).text()) + '\' target=\'_blank\'>' + $(this).text() + '</a>';
            $(this).html(link);
          });
          $invoiceBtn = $('#modal-invoice-btn');
          if (event.invoice_id !== null) {
            $invoiceBtn.html('Edit Invoice');
            $invoiceBtn.attr('href', '/invoices/' + event.invoice_id + '/edit');
          } else {
            $invoiceBtn.html('Create Invoice');
            $invoiceBtn.attr('href', '/invoices/new?event_id=' + event.id);
          }
          $('#event-modal').openModal();
        });
        if (event.city) {
          element.find('.fc-title').append("<br>[" + event.city + "]");
        }
      },
      dayClick: function(date) {
        window.location = '/events/new?start_time=' + encodeURIComponent(date.format());
      }
    });
  });

}).call(this);
$(document).ready(function(){
  // Disable Submit on Enter

  //$(".disable-submit").bind("keypress", function(e) {
  //  if (e.keyCode == 13) return false;
  //});

  // Initialize collapse button
  $(".button-collapse").sideNav();

  // Make flash disapear
  //
  setTimeout(function(){
    $(".flash").fadeOut();
  }, 3000);

  // Activate Select
  $('select').material_select();

  // Activate Datepicker
  $('.datepicker').pickadate({
      selectMonths: false,
      selectYears: false,
      clear: false,
      format: 'mmmm d, yyyy'
    });

  // Slider
   $('.slider').slider({indicators: false});

  // Activate Collapsible
  $('.collapsible-body').collapsible();

  // TypeAhead

  var customers = new Bloodhound({
    datumTokenizer: Bloodhound.tokenizers.obj.whitespace('id', 'title'),
    queryTokenizer: Bloodhound.tokenizers.whitespace,
    local: gon.customers
  });

  $('.typeahead').typeahead(null, {
    displayKey: 'title',
    valueKey: 'id',
    source: customers,
    highlight: true,
    hint: true,
    limit: 10,
    templates: {
      empty: [
        '<div>',
          'No customers found',
        '</div>'
      ].join('\n'),
      suggestion: function(e) { return ('<div>' + e.title + '</div>') }
    }
  });

  $('input#search').on('typeahead:autocompleted typeahead:selected', function (e, datum) {
    var customerID = datum['id'];
    $('#customer_id').val(customerID);
  });

  // Convert <address> tags to Google links
  $('address').each(function () {
    var link = "<a href='http://maps.google.com/maps?q=" + encodeURIComponent( $(this).text() ) + "' target='_blank'>" + $(this).text() + "</a>";
    $(this).html(link);
  });
});
