(function() {
  $(function() {
    var calculateSubTotal, calculateTotal, formatCurrency, reminderDate;
    reminderDate = $('#invoice_reminder_date');
    $('#reminder-3m').click(function(event) {
      var reminder;
      event.preventDefault();
      reminder = moment(Date.now());
      reminder = reminder.add(3, 'months');
      reminder = reminder.format('D MMMM, YYYY');
      return reminderDate.val(reminder);
    });
    $('#reminder-6m').click(function(event) {
      var reminder;
      event.preventDefault();
      reminder = moment(Date.now());
      reminder = reminder.add(6, 'months');
      reminder = reminder.format('D MMMM, YYYY');
      return reminderDate.val(reminder);
    });
    $('#reminder-1y').click(function(event) {
      var reminder;
      event.preventDefault();
      reminder = moment(Date.now());
      reminder = reminder.add(1, 'year');
      reminder = reminder.format('D MMMM, YYYY');
      return reminderDate.val(reminder);
    });
    $('#invoice-items').bind('cocoon:after-insert', function(e, insertedItem) {
      insertedItem.find('.material-select').material_select();
      return $('.total-cost').bind('blur input', function() {
        calculateTotal();
      });
    });
    $('#invoice-items').bind('cocoon:after-remove', function(e, insertedItem) {
      return calculateTotal();
    });
    $('#invoice_discount_id').change(function() {
      return calculateTotal();
    });
    calculateSubTotal = function() {
      var sum;
      sum = 0;
      $('.total-cost').each(function() {
        return sum += Number($(this).val());
      });
      $('#sub-total').val(formatCurrency(sum));
      return sum;
    };
    calculateTotal = function() {
      var discount, subTotal, total;
      discount = $('#invoice_discount_id').find(':selected').data('discount');
      discount = discount || 0;
      subTotal = calculateSubTotal();
      total = subTotal - discount;
      total = formatCurrency(total);
      return $('#invoice_total').val(total);
    };
    return formatCurrency = function(num) {
      var cents, i, sign;
      num = num.toString().replace(/\$|\,/g, '');
      if (isNaN(num)) {
        num = '0';
      }
      sign = num === (num = Math.abs(num));
      num = Math.floor(num * 100 + 0.50000000001);
      cents = num % 100;
      num = Math.floor(num / 100).toString();
      if (cents < 10) {
        cents = '0' + cents;
      }
      i = 0;
      while (i < Math.floor((num.length - (1 + i)) / 3)) {
        num = num.substring(0, num.length - (4 * i + 3)) + ',' + num.substring(num.length - (4 * i + 3));
        i++;
      }
      return num + '.' + cents;
    };
  });

}).call(this);
